import{_ as o,c,o as d,b0 as t}from"./chunks/framework.D8aj9ziZ.js";const _=JSON.parse('{"title":"EggJs 学习指南","description":"","frontmatter":{"title":"EggJs 学习指南"},"headers":[],"relativePath":"BE/node/eggjs/index.md","filePath":"BE/node/eggjs/index.md","lastUpdated":1749894304000}'),s={name:"BE/node/eggjs/index.md"};function a(i,e,r,l,g,n){return d(),c("div",null,e[0]||(e[0]=[t('<p><code>Egg.js</code> 是一个基于 <code>Node.js</code> 的企业级框架，主要用于构建高性能的 <code>Web</code> 应用和 <code>API</code>。</p><p>它由阿里巴巴团队开发，旨在提供一个灵活、可扩展的开发环境，适合大型项目的需求。</p><h3 id="_1-特性" tabindex="-1">1. 特性 <a class="header-anchor" href="#_1-特性" aria-label="Permalink to &quot;1. 特性&quot;">​</a></h3><ul><li><strong>插件化架构</strong>：<code>Egg.js</code> 采用插件化的设计，允许开发者根据需要选择和使用不同的插件，增强应用的功能。</li><li><strong>中间件支持</strong>：<code>Egg.js</code> 支持中间件，可以在请求处理的不同阶段插入自定义逻辑，方便进行请求处理、日志记录、权限验证等操作。</li><li><strong>强大的路由系统</strong>：<code>Egg.js</code> 提供了灵活的路由配置，支持 RESTful 风格的 API 设计，方便管理和组织路由。</li><li><strong>高性能</strong>：基于 <code>Koa.js</code>，<code>Egg.js</code> 继承了 <code>Koa</code> 的高性能特性，能够处理大量并发请求。</li><li><strong>内置支持 TypeScript</strong>：<code>Egg.js</code> 原生支持 <code>TypeScript</code>，方便开发者使用类型系统来提高代码的可维护性和可读性。</li></ul><h3 id="_2-目录结构" tabindex="-1">2. 目录结构 <a class="header-anchor" href="#_2-目录结构" aria-label="Permalink to &quot;2. 目录结构&quot;">​</a></h3><p><code>Egg.js</code> 的项目结构清晰，通常包括以下几个主要目录：</p><ul><li><code>app</code>：应用的核心代码，包括控制器、服务、路由等。</li><li><code>config</code>：应用的配置文件，包括环境配置、插件配置等。</li><li><code>public</code>：静态资源文件，如图片、<code>CSS</code>、<code>JavaScript</code> 等。</li><li><code>test</code>：测试代码，支持单元测试和集成测试。</li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>完整结构可查看<a href="https://www.eggjs.org/zh-CN/basics/structure" target="_blank" rel="noreferrer">egg目录结构</a>。</p></div>',8)]))}const u=o(s,[["render",a]]);export{_ as __pageData,u as default};
